def can_divide_into_equal_groups(A, B, C):
    # Calculate the total sum of the three integers
    S = A + B + C

    # Check if the total sum is even
    if S % 2 != 0:
        return "No"

    # Calculate the target sum for each group
    target_sum = S // 2

    # Helper function to check if a subset sums up to the target sum
    def is_subset_sum(arr, n, target):
        # Create a 2D array to store subproblem solutions
        dp = [[False] * (target + 1) for _ in range(n + 1)]

        # Base case: empty subset always has a sum of 0
        for i in range(n + 1):
            dp[i][0] = True

        # Fill the DP table
        for i in range(1, n + 1):
            for j in range(1, target + 1):
                if arr[i - 1] <= j:
                    dp[i][j] = dp[i - 1][j] or dp[i - 1][j - arr[i - 1]]
                else:
                    dp[i][j] = dp[i - 1][j]

        return dp[n][target]

    # Check if there exists a subset that sums up to the target sum
    if is_subset_sum([A, B, C], 3, target_sum):
        return "Yes"
    else:
        return "No"

