def is_full_house(cards):
    # Count the frequency of each integer
    freq = {}
    for card in cards:
        if card in freq:
            freq[card] += 1
        else:
            freq[card] = 1

    # Check if there are exactly two distinct integers with frequencies of 3 and 2
    count_3 = sum(1 for count in freq.values() if count == 3)
    count_2 = sum(1 for count in freq.values() if count == 2)

    return count_3 == 1 and count_2 == 1

