import sys

def main():
    data = sys.stdin.read().splitlines()
    n = int(data[0])
    strings = [line.strip() for line in data[1:1+n]]

    # Find maximum length
    max_len = max(len(s) for s in strings)

    # Initialize result list with empty strings
    result = []
    for _ in range(max_len):
        result.append("")

    # Process each character position
    for pos in range(max_len):
        # Check if we can place '*' at this position
        valid = True
        for i in range(1, n + 1):
            if pos >= len(strings[i - 1]):
                continue
            char_at_pos = strings[i - 1][pos]
            if char_at_pos != '*':
                valid = False
                break

        if valid:
            # We can place '*', so do it
            for i in range(1, n + 1):
                if pos < len(strings[i - 1]):
                    result[pos] += strings[i - 1][pos]
                else:
                    result[pos] += '*'
        else:
            # We cannot place '*', so fill with spaces
            for i in range(1, n + 1):
                if pos < len(strings[i - 1]):
                    result[pos] += strings[i - 1][pos]
                else:
                    result[pos] += ' '

    # Print the result
    print("\n".join(result))

