def main():
    data = sys.stdin.read().split()
    n = int(data[0])
    graph = [[] for _ in range(n+1)]

    index = 1
    for i in range(1, n+1):
        for j in range(i, n+1):
            a = int(data[index]); b = int(data[index+1])
            index += 2
            if a == i:
                graph[i].append(b)
            else:
                graph[b].append(a)

    visited = [False] * (n+1)
    stack = []
    current = 1

    while True:
        if not visited[current]:
            visited[current] = True
            stack.append(current)
            for neighbor in graph[current]:
                if not visited[neighbor]:
                    current = neighbor
                    break
            else:
                # Found cycle
                result = current
                while stack and stack[-1] != result:
                    visited[stack.pop()] = False
                if stack:
                    current = stack[-1]
                else:
                    break
        else:
            current = stack.pop()

    print(result)

