def main():
    S = input().strip()

    # Precompute all prefixes for each digit
    prefix_dict = {}
    for i, char in enumerate(S):
        if char not in prefix_dict:
            prefix_dict[char] = []
        prefix_dict[char].append(i)

    # Initialize DP array: dp[i][j] where j is the last digit used
    n = len(S)
    dp = [[float('inf')] * 10 for _ in range(n + 1)]

    # Base case: no digits used yet
    for i in range(10):
        dp[0][i] = 0

    # Fill DP table
    for i in range(1, n + 1):
        for j in range(10):
            if S[i - 1] == str(j):
                dp[i][j] = min(dp[i][j], dp[i - 1][j])
            else:
                for k in prefix_dict.get(str(j), []):
                    if k < i:
                        dp[i][j] = min(dp[i][j], dp[k][j] + i - k)

    result = min(dp[n])
    print(result)

