def main():
    A, B = map(int, input().split())

    count = 0

    # Check all possible differences d
    for d in range(-100, 101):
        if abs(d) > (A - B):
            continue

        # Calculate candidate x values
        candidates = []

        # Case 1: A is middle term
        if A - d == B + d:
            candidates.append(A + d)

        # Case 2: B is middle term  
        if B - d == A + d:
            candidates.append(B + d)

        # Case 3: x is between A and B  
        if A < B:
            if A - d < B and B - d < A + d:
                candidates.append(2 * B - A - d)
        else:
            if A - d > B and B - d > A + d:
                candidates.append(2 * B - A - d)

        # Add valid candidates to count
        for cand in candidates:
            if 1 <= cand <= 100:
                count += 1

    print(count)

